import socket


HOST = '10.7.180.29' 
PORT = 42169
if __name__ == "__main__":
    while True:
        #rawInput=input("input data to send: ")
        rawInput = ""
        outdata = bytes(rawInput,'utf-8')

        with socket.socket(socket.AF_INET, socket.SOCK_STREAM) as s:
            s.connect((HOST, PORT))
            while True:
                s.sendall(outdata)
                indata = s.recv(1024)
                
                print('Received'+ repr(indata))

    